INSERT INTO `posts` (`id`, `title`, `descc`, `content`, `updated_at`, `created_at`, `deleted_at`, `post_cat_id`, `status`, `img_post`) VALUES
(1, 'Tái khởi động đường bay chở khách quốc tế', '<p>Cục H&agrave;ng kh&ocirc;ng Việt Nam đ&atilde; l&ecirc;n kế hoạch mở lại đường bay thương mại chở kh&aacute;ch quốc tế với 4 giai đoạn, dự kiến bắt đầu cuối năm nay.</p>', '<h1 class=\"title-detail\">T&aacute;i khởi động đường bay chở kh&aacute;ch quốc tế</h1>\r\n<p class=\"description\">Cục H&agrave;ng kh&ocirc;ng Việt Nam đ&atilde; l&ecirc;n kế hoạch mở lại đường bay thương mại chở kh&aacute;ch quốc tế với 4 giai đoạn, dự kiến bắt đầu cuối năm nay.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 22/10, đại diện Cục H&agrave;ng kh&ocirc;ng Việt Nam cho hay, theo kế hoạch tr&igrave;nh Bộ Giao th&ocirc;ng Vận tải, ng&agrave;nh sẽ khởi động c&aacute;c chuyến bay quốc tế trọn g&oacute;i (combo) cho người Việt Nam về nước, tổ chức chuyến bay th&iacute; điểm đ&oacute;n du kh&aacute;ch quốc tế đến một số địa phương như Ph&uacute; Quốc, Kh&aacute;nh H&ograve;a, Quảng Ninh...</p>\r\n<p class=\"Normal\"><img src=\"/unitop.vn/back-end/lavarel/project/smartkey/public//storage/photos/1/images/anh-6610-1634866266.jpg\" alt=\"\" /></p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<figcaption>\r\n<p class=\"Image\">M&aacute;y bay đỗ tại s&acirc;n bay T&acirc;n Sơn Nhất. Ảnh:<em>&nbsp;Hữu Khoa</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Với chuyến bay th&iacute; điểm đ&oacute;n du kh&aacute;ch quốc tế, Cục H&agrave;ng kh&ocirc;ng Việt Nam đề xuất giai đoạn 1 từ cuối năm nay, kh&ocirc;ng giới hạn thị trường. Du kh&aacute;ch v&agrave;o Việt Nam cần c&oacute; kết quả x&eacute;t nghiệm &acirc;m t&iacute;nh bằng PCR trong 72 giờ trước chuyến đi, đ&atilde; ti&ecirc;m đủ liều vaccine ph&ograve;ng Covid-19. Trong đ&oacute;, liều cuối c&ugrave;ng ti&ecirc;m &iacute;t nhất 14 ng&agrave;y v&agrave; kh&ocirc;ng qu&aacute; 12 th&aacute;ng t&iacute;nh đến thời điểm xuất ph&aacute;t; hoặc c&oacute; giấy x&aacute;c nhận khỏi bệnh Covid-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng t&iacute;nh đến thời điểm xuất ph&aacute;t v&agrave; đăng k&yacute; tham gia chương tr&igrave;nh du lịch trọn g&oacute;i của doanh nghiệp lữ h&agrave;nh.</p>\r\n<p class=\"Normal\">Tần suất khai th&aacute;c một chuyến bay mỗi ng&agrave;y (khoảng 4.000-6.000 lượt kh&aacute;ch đến trong th&aacute;ng đầu ti&ecirc;n) v&agrave; tăng l&ecirc;n 2 chuyến bay trở l&ecirc;n trong th&aacute;ng tiếp theo.</p>\r\n<p class=\"Normal\">Trong giai đoạn 2 (từ th&aacute;ng 1/2022), Cục H&agrave;ng kh&ocirc;ng Việt Nam đề xuất th&iacute; điểm c&aacute;c chuyến bay thường lệ quốc tế chỉ chở kh&aacute;ch c&oacute; giấy chứng nhận đ&atilde; ti&ecirc;m đủ liều vaccine hoặc c&oacute; x&aacute;c nhận khỏi bệnh trước khi v&agrave;o Việt Nam.</p>\r\n</article>', '2021-10-22 02:59:00', '2021-10-22 02:06:46', NULL, 11, '1', 'anh-1634865886-5939-1634866266.jpg'),
(2, 'Trần Cảnh Đôn một đời đi tìm \'ngọc trong đá\'', '<p>Trước khi qua đời ở tuổi 62, đạo diễn Trần Cảnh Đ&ocirc;n ghi dấu ở d&ograve;ng phim thị trường, tạo bệ ph&oacute;ng cho Việt Trinh, L&yacute; H&ugrave;ng...</p>', '<div class=\"social_pin\">\r\n<h1 class=\"title-detail\">Trần Cảnh Đ&ocirc;n một đời đi t&igrave;m \'ngọc trong đ&aacute;\'</h1>\r\n</div>\r\n<div class=\"sidebar-1\">\r\n<p class=\"description\">Trước khi qua đời ở tuổi 62, đạo diễn Trần Cảnh Đ&ocirc;n ghi dấu ở d&ograve;ng phim thị trường, tạo bệ ph&oacute;ng cho Việt Trinh, L&yacute; H&ugrave;ng...</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Tin đạo diễn Trần Cảnh Đ&ocirc;n đột ngột&nbsp;<a href=\"https://vnexpress.net/dao-dien-tran-canh-don-qua-doi-4375264.html\" rel=\"dofollow\">qua đời</a>&nbsp;tối 21/10 khiến giới nghệ sĩ b&agrave;ng ho&agrave;ng. &Aacute; hậu Trịnh Kim Chi - đ&agrave;n em gắn b&oacute; anh từ ng&agrave;y đầu v&agrave;o nghề - v&iacute; đạo diễn như một người d&agrave;nh cả đời đi t&igrave;m \"ngọc trong đ&aacute;\", từ đ&oacute; ph&aacute;t hiện những t&agrave;i năng của l&agrave;ng phim miền Nam.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_19hIlAf8mXCG_A3l1rYoQ 1x, https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o1EaFk2Vnby65ok71Waigg 1.5x, https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=LC3MovfPXArWFtA2qPpDsg 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_19hIlAf8mXCG_A3l1rYoQ 1x, https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o1EaFk2Vnby65ok71Waigg 1.5x, https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=LC3MovfPXArWFtA2qPpDsg 2x\" /><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_19hIlAf8mXCG_A3l1rYoQ\" alt=\"Đạo diễn Trần Cảnh Đ&ocirc;n (1959 - 2021). Ảnh: Facebook Tran Canh Don\" data-src=\"https://i1-giaitri.vnecdn.net/2021/10/22/dao-dien-tran-canh-don-1-6833-1634883996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_19hIlAf8mXCG_A3l1rYoQ\" data-ll-status=\"loaded\" data-adbro-processed=\"true\" /></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đạo diễn Trần Cảnh Đ&ocirc;n (1959 - 2021). Ảnh:<em>&nbsp;Facebook Tran Canh Don</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đạo diễn khởi nghiệp phim ảnh c&oacute; phần chật vật. Y&ecirc;u nghệ thuật nhưng giỏi c&aacute;c m&ocirc;n tự nhi&ecirc;n, ban đầu Trần Cảnh Đ&ocirc;n theo đuổi ng&agrave;nh t&agrave;i ch&iacute;nh - kế to&aacute;n. Ra trường, cầm tấm bằng tốt nghiệp đại học tr&ecirc;n tay, anh nhận ra bản th&acirc;n đi sai hướng. Ch&agrave;ng trai 24 tuổi khi ấy quyết định l&agrave;m lại, thi v&agrave;o trường Nghệ thuật S&acirc;n khấu 2 (nay l&agrave; Đại học S&acirc;n khấu Điện ảnh TP HCM), đỗ v&agrave;o khoa đạo diễn s&acirc;n khấu, c&ugrave;ng kh&oacute;a với Minh Nh&iacute;, H&ugrave;ng L&acirc;m... Ra trường với \"vốn dắt lưng\" l&agrave; hai vở kịch tốt nghiệp&nbsp;<em>M&ugrave;a t&ocirc;m</em>&nbsp;v&agrave;<em>&nbsp;Cuộc phi&ecirc;u lưu của những t&acirc;m hồn</em>, anh đi học điện ảnh, từ đ&oacute; dấn th&acirc;n v&agrave;o con đường l&agrave;m phim.</p>\r\n<p class=\"Normal\">&nbsp;</p>\r\n<p class=\"Normal\">&nbsp;</p>\r\n</article>\r\n</div>', '2021-10-22 02:59:13', '2021-10-22 02:13:35', NULL, 11, '1', 'dao-dien-tran-canh-don-1-16348-7818-5012-1634883996.jpg');

